import React, { useState, useEffect  } from 'react'
import useMobilDetect from '../mobilHook'
import Video from '../../videos/video.mp4'
import Button from '../buttons/button'
// import Image from '../../images/pexels-andrea-piacquadio-3928268.jpg'
import Image_1 from '../../images/pexels-andrea-piacquadio-3928268.jpg'
import Image_2 from '../../images/pexels-andrea-piacquadio-640X494.jpg'


const HeroSection = () => {
const [esMobil, setEsMobil ] = useState(null)

    useEffect(()=> {
        const mql = window.matchMedia('min-width: 768px)')
        mql.addEventListener("change", () => {
            if (mql.matches){
                setEsMobil(false)
            }else{
                setEsMobil(true)
            }
        })
    },[])


    if (!esMobil) {
        return (
            <>
            <div className="heroContainerMobil">
                <div className="heroContentMobil"></div>

            </div>
            <div className="heroBGMobil">
                 <img
                   className="imageBGMobil"
                   alt="this is a test"
                   src={Image_2}
                   />
            </div>


                {/* <div className="heroContentMobil">
                    <h1 className="heroH1Mobil">La nueva forma de comprar sin miedo</h1>
                    <h1 className="heroH1Mobil">por la deep web</h1>
                    <div className="heroBtnWrapperMobil">
                       <Button 
                        btnBG={ '#FAD570' }
                        fontColor={ true }
                        big={ true }
                        fontBig = { true }
                        >
                        </Button>
                    </div>
                </div> */}
            </>        
        )
    }

    return (
        <div className="heroContainer">
            <div className="heroBG">
                <img
                   className="imageBG"
                   alt="this is a test"
                //    src={Image_1}
                   src={`${!esMobil ? Image_1 : Image_2}`}

                   />
             </div>

                <div className="heroContent">
                    <h1 className="heroH1">La nueva forma de comprar sin miedo</h1>
                    <h1 className="heroH1">por internet</h1>
                    <div className="heroBtnWrapper">
                       <Button 
                        btnBG={ '#FAD570' }
                        fontColor={ true }
                        big={ true }
                        fontBig = { true }
                        >
                        </Button>
                    </div>
                </div>
        </div>
    )
}


export default HeroSection
