import React, { useState } from 'react'

const InterestedUsersForm = ({ handlingSubmitUser }) => {

    const [fullName, setFullName] = useState('')
    const [email, setEmail] = useState('')

    const onHandlingName = (e) => {
        setFullName(e.target.value)
        console.log(fullName)

    }

    const onHandlingEmail = (e) => {
        setEmail(e.target.value)
        console.log(email)

    }

    const handlingSubmit = (e) => {
        e.preventDefault()
        handlingSubmitUser(fullName, email)
        setFullName('')
        setEmail('')
    }

    return (
        <>
                <form className="form-1"  
        onSubmit={(e)=> handlingSubmit(e)}
        >
            <h1 className="dataH1">¿Quieres saber más de nosotros? </h1>

            <input
                onChange={ (e) => onHandlingName(e)} 
                type="text" 
                placeholder="Nombre completo"
                required
                value={fullName}

                />
                 <input
                onChange={ (e) => onHandlingEmail(e)} 
                type="text" 
                placeholder="Correo electrónico"
                required
                value={email}

                />
                  <input
                onChange={ (e) => onHandlingEmail(e)} 
                type="text" 
                placeholder="Ciudad"
                required
                // value={city}

                />
                <button
                onClick={(e) => handlingSubmit(e) } 
                type="submit">Enviar</button>  
        </form>
        </>




    )
}

export default InterestedUsersForm
