import React, { useState } from 'react'
import MobilDetect from '../mobilHook'
import Video from '../../videos/video.mp4'
import Button from '../buttons/button'
// import Image from '../../images/pexels-andrea-piacquadio-3928268.jpg'
import Image_1 from '../../images/pexels-andrea-piacquadio-3928268.jpg'
import Image_2 from '../../images/pexels-andrea-piacquadio-main_medium.jpg'


const HeroSection = () => {

    // const itsMobil = MobilDetect()
    // console.log(itsMobil)
    return (
        <div className="heroContainer">
             <div className="heroBG">
                <img
                   className="imageBG"
                   alt="this is a test"
                   src={`${ MobilDetect() ? Image_1 : Image_2 }`}/>
                    {/* src={Image_1}/> */}
             </div>
             <div className="heroContent">
                <h1 className="heroH1">La nueva forma de comprar sin miedo</h1>
                <h1 className="heroH1">por internet</h1>
                <div className="heroBtnWrapper">
                    <Button 
                    btnBG={ '#FAD570' }
                    fontColor={ true }
                    big={ true }
                    fontBig = { true }
                    >
                    </Button>
                </div>
             </div>
        </div>
    )
}


export default HeroSection
