import React from 'react'
import { useMobilDetection, useMobilDetect} from '../../utils/mobilDetection'

import { 
    HeroContainer,
    HeroBG,
    ImageBG,
    HeroContent,
    HeroH1,
    HeroBtnWrapper


} from './styledHeroSectionElements'
import Image from '../../images/pexels-andrea-piacquadio-3928268.jpg'
import { StyledButton } from '../buttons/styledButton'


const StyledHeroSection = () => {
const mobil = useMobilDetect()
const mobil2 = useMobilDetection()

if (mobil2.screenWidth < 768 || mobil) {
    return (
        <HeroContainer>
            <HeroBG>
                <ImageBG
                src={Image}
                />
            </HeroBG>
            <HeroContent>
                <HeroH1>Somos la nueva forma de comprar sin miedo</HeroH1>
                <HeroH1>por internet</HeroH1>
                <HeroBtnWrapper>
                    <StyledButton
                    primary={ true }
                    dark={ true }
                    >
                        Ver video
                    </StyledButton>
                </HeroBtnWrapper>
            </HeroContent>
        </HeroContainer>
    )
}
if (mobil2.screenWidth > 768 || !mobil) {
    return (
        <HeroContainer>
            <HeroBG>
                <ImageBG
                src={Image}
                />
            </HeroBG>
            <HeroContent>
                <HeroH1>Somos la nueva forma de comprar sin miedo</HeroH1>
                <HeroH1>por internet</HeroH1>
                <HeroBtnWrapper>
                    <StyledButton
                    primary={ true }
                    dark={ true }
                    >
                        Ver video
                    </StyledButton>
                </HeroBtnWrapper>
            </HeroContent>
        </HeroContainer>
    )
}

}

export default StyledHeroSection
