import React from 'react'
import { FaBars,FaSign, FaUserPlus } from 'react-icons/fa'
import { Link as LinkR } from 'react-router-dom'
import { Link as LinkS } from 'react-scroll'
import { IoMdCart } from 'react-icons/io'
import { BiUser, BiUserCheck, BiRun } from 'react-icons/bi'
import useMobilDetection from '../../utils/mobilDetection'


const NavBarMobil = ({ 
    toggleSideBar, 
    login, 
    toggleLoginSideBar,
    toggleMainSideBar 
}) => {
    const mobil2 = useMobilDetection() 
    console.log(mobil2)

    const settingOffset = (option) => {
        if (option === 'notifyMe'){
            if (mobil2.screenWidth <= 1098 && mobil2.screenWidth > 768){
                return 1220
            }
            if (mobil2.screenWidth <= 768 && mobil2.screenWidth > 560 ){
                return 1025
            }
            if (mobil2.screenWidth <= 560 && mobil2.screenWidth > 480){
                return 1550
            }
            if (mobil2.screenWidth <= 480 && mobil2.screenWidth > 414){
                return 1548
            }
            if (mobil2.screenWidth <= 414 && mobil2.screenWidth > 360){
                return 1650
            }
            if (mobil2.screenWidth <= 360 && mobil2.screenWidth > 280){
                return 1450
            }
        }
        if (option === 'startNow'){
            if (mobil2.screenWidth <= 1098 && mobil2.screenWidth > 768){
                return 1200
            }
            if (mobil2.screenWidth <= 768 && mobil2.screenWidth > 560 ){
                return 1025
            }
            if (mobil2.screenWidth <= 560 && mobil2.screenWidth > 480){
                return 1550
            }
            if (mobil2.screenWidth <= 480 && mobil2.screenWidth > 414){
                return 1548
            }
            if (mobil2.screenWidth <= 414 && mobil2.screenWidth > 360){
                return 1650
            }
            if (mobil2.screenWidth <= 360 && mobil2.screenWidth > 280){
                return 1450
            }
        }

    }

        return (
            <>
                <nav className="navMobil" >
                <div className="navBarContainerMobil">
                    <LinkR to="/" className="navLogoMobil">sh3ck</LinkR>
                    <div 
                    // onClick={ toggleSideBar }
                    className="mobileIconMobil">
                        {/* <FaRegUser className="faIcon"/> */}
                        {login ?
                        <BiUserCheck 
                        onClick={toggleMainSideBar}
                        className="faUserIcon"/> 
                         :   
                        <BiUser 
                        onClick={toggleLoginSideBar}
                        className="faUserIcon" />
                        }
                        <FaBars 
                        onClick={toggleSideBar}
                        className="faBarsIcon"/>
                    </div>
                </div>
                <div className="navMenuButtonsContainer">
                    <div className="navMenuButton">
                        <LinkS 
                        to="contactSection" 
                        className="navLinksMobil" 
                        activeClass="active"
                        spy={true}
                        smooth={true}
                        // offset={1220}
                        offset= { settingOffset('notifyMe')
                            // mobil2.screenWidth <= 1098 && mobil2.screenWidth > 768  ? 1220 : 
                            // mobil2.screenWidth <= 768 && mobil2.screenWidth > 560  ? 1025 :
                            // mobil2.screenWidth <= 560 && mobil2.screenWidth > 480  ? 1550 :
                            // mobil2.screenWidth <= 480 && mobil2.screenWidth > 414  ? 1548 :
                            // mobil2.screenWidth <= 414 && mobil2.screenWidth > 360  ? 1650 :
                            // mobil2.screenWidth <= 360 && mobil2.screenWidth > 280  ? 1450 : 1000
                        }


                        duration={1000}
                        >
                            <div className="iconMobilContainer">
                                <FaSign className="faIcon"/>
                            </div>
                            Notificame
                        </LinkS>

                    </div>
                    <div className="navMenuButton">
                        <LinkS 
                        to="contactSection" 
                        className="navLinksMobil" 
                        activeClass="active"
                        spy={true}
                        smooth={true}
                        offset={settingOffset('startNow')}
                        duration={1000}
                        >
                        <div className="iconMobilContainer">
                                <BiRun className="faIcon"/>
                        </div>
                        Comienza
                        </LinkS>
                    </div>
                    <div className="navMenuButton">
                        <LinkS 
                        to="contactSection" 
                        className="navLinksMobil" 
                        activeClass="active"
                        spy={true}
                        smooth={true}
                        offset={1650}
                        duration={1000}
                        >
                        <div className="iconMobilContainer">
                                <IoMdCart className="faIcon"/>
                        </div>
                        Chequéa
                        </LinkS>
                    </div>

                </div>
            </nav>
            </>
        )
}







export default NavBarMobil
