import { useState, useEffect } from 'react'


const MobilDetect = () => {
const [ itsMobil, setItsMobil ] = useState(null)

    useEffect(()=> {
        const mql = window.matchMedia('(min-width: 1098px)')
        mql.addEventListener("change", () => {
            if (mql.matches){
                setItsMobil(false)
            }else{
                setItsMobil(true)
            }
        })


    //     return ()=> {
    //         removeEventListener("change")  
    // }
    },[])

    return itsMobil
}

export default MobilDetect
